import React from 'react';
import './App.css';
import Header from './components/Header.js';
import ChooseBlock from './components/ChooseBlock.js';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.handleSave = this.handleSave.bind(this)
    this.handleReload = this.handleReload.bind(this)
    this.handleUpdateElementActive = this.handleUpdateElementActive.bind(this);
    this.state = {
      to_do_list: []
    };
  }

  componentDidMount() {
    this.handleReload();
  }

  handleSave() {
    fetch('/api/upload-data', {
      method: "POST",
      body: JSON.stringify(this.state),
      headers: {
        'Content-type': 'application/json; charset=UTF-8',
      }
    })
  }

  handleReload() {
    fetch('/api/get-ideas')
      .then(res => res.json())
      .then(res => this.setState({to_do_list: res.ideas}))
  }

  handleUpdateElementActive(e) {
    e.status = "active";
    this.state.to_do_list.push(e);
    this.setState({to_do_list: this.state.to_do_list});
  }

  handleUpdateElementCompleted(e) {

    for( let i = 0; i < this.state.to_do_list.length; i++ ) {
      if( this.state.to_do_list[i]._id === e._id ) {
        this.state.to_do_list[i].status="completed";
      }
    }

    this.setState({to_do_list: this.state.to_do_list});
  }

  render() {
    return (
      <div className="App">
        <Header onSave={this.handleSave} onReload={this.handleReload} />
        <ChooseBlock onUpdateToDoList={this.handleUpdateElementActive} />
      </div>
    )
  }
}

export default App;
