import React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import Tooltip from '@mui/material/Tooltip';
import ContentBlock from './ContentBlock.js';
import Title from './Title.js';
import '../css/carousel.css';

function ActionAreaCard(props) {
  return (
    <Card className="carouselCard" id={props.id} style={{margin: "auto"}} sx={{ maxWidth: 345 }}>
      <Tooltip title="Add idea to goal list?" arrow followCursor>
        <CardActionArea onClick={props.onClick} className="carouselCardArea">
          <CardContent className="carouselCardContentArea">
            <Typography className="carouselCardContent" align="center" variant="body1" color="text.secondary" component="div">
              {props.content}
            </Typography>
            <Typography mt="1" align="center" gutterBottom variant="h5" component="div">
              {props.title}
            </Typography>
          </CardContent>
        </CardActionArea>
      </Tooltip>
    </Card>
  );
}

class CarouselBlock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {current: 0};
  }

  handleUpdate(e) {
    this.props.onUpdateToDoList(this.state.ideas[e]);
  }

  render() {
    let content;

    if( this.props.ideas.length !== 0 ) {    
      let ideasList = [];
      let prevSlide;
      let centerSlide;
      let nextSlide;

      for( let i = 0; i < this.props.ideas.length; i++ ) {
        if( this.props.ideas[i].status === "active" ) {
          ideasList.push( this.props.ideas[i] );
        }
      }

      if( this.state.current === 0 ) {
        prevSlide = ideasList.length-1;
        centerSlide = this.state.current;
        nextSlide = this.state.current +1;
      } else if( this.state.current === ideasList.length-1 ) {
        prevSlide = this.state.current - 1;
        centerSlide = this.state.current;
        nextSlide = 0;
      } else {
        prevSlide = this.state.current - 1;
        centerSlide = this.state.current;
        nextSlide = this.state.current + 1;
      }

      let prevElem;
      let centerElem;
      let nextElem;

      if(ideasList[prevSlide] !== 'undefiend') {
        prevElem = <ActionAreaCard id="carousel_elem_outcenter" title={ideasList[prevSlide].title} content={ideasList[prevSlide].content} />;
      }
      if(ideasList[centerSlide] !== 'undefiend') {
        centerElem = <ActionAreaCard id="carousel_elem_center" title={ideasList[centerSlide].title} content={ideasList[centerSlide].content} />;
      }
      if(ideasList[nextSlide] !== 'undefiend') {
        nextElem = <ActionAreaCard id="carousel_elem_outcenter" title={ideasList[nextSlide].title} content={ideasList[nextSlide].content} />
      }

      content = (
        <div className="carousel">
          <div>
            <button>Prev</button>
            <div className="carousel_list">
              {prevElem}{centerElem}{nextElem}
            </div>
            <button>Next</button>
          </div>
          <div style={{margin: "1em"}}>{`${this.state.current+1} / ${ideasList.length}`}</div>
        </div>
      );
    } else {
      content = (
        <div className="carousel">
          <div className="carousel_list">
            <Box style={{marginTop: "5em"}} sx={{ display: 'flex' }}><CircularProgress /></Box>;
          </div>
        </div>
      );
    }

    return (
      <ContentBlock>
        <Title content="Ideas in my list" />
        {content}
        <Typography mt="1" align="center" gutterBottom variant="h5" component="div">
        </Typography>
      </ContentBlock>
    )
  }
}

export default CarouselBlock;
