import React from 'react';
import './App.css';
import Header from './components/Header.js';
import ChooseBlock from './components/ChooseBlock.js';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.handleSave = this.handleSave.bind(this)
    this.handleReload = this.handleReload.bind(this)
    this.handleUpdateToDoList = this.handleUpdateToDoList.bind(this);
    this.state = {
      to_do_list: []
    };
  }

  componentDidMount() {
    fetch('/api/get-ideas')
      .then(res => res.json())
      .then(res => this.setState({to_do_list: res.ideas}))
  }

  handleSave() {
    fetch('/api/upload-data', {
      method: "POST",
      body: JSON.stringify(this.state),
      headers: {
        'Content-type': 'application/json; charset=UTF-8',
      }
    })
  }

  handleReload() {
    console.log(this.state)
    fetch('/api/reload-data', {
      method: "POST",
      body: JSON.stringify(this.state),
      headers: {
        'Content-type': 'application/json; charset=UTF-8',
      }
    })
  }

  handleUpdateToDoList(e) {
    e.status = "active";
    this.state.to_do_list.push(e);
    this.setState({to_do_list: this.state.to_do_list});
  }

  render() {

    console.log(this.state)
    return (
      <div className="App">
        <Header onSave={this.handleSave} onReload={this.handleReload} />
        <ChooseBlock onUpdateToDoList={this.handleUpdateToDoList} />
      </div>
    )
  }
}

export default App;
