import React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import Tooltip from '@mui/material/Tooltip';
import ContentBlock from './ContentBlock.js';
import Title from './Title.js';
import '../css/carousel.css';

function ActionAreaCard(props) {
  return (
    <Card className="chooseCard" style={{margin: "1em"}} sx={{ maxWidth: 345 }}>
      <Tooltip title="Add idea to goal list?" arrow followCursor>
        <CardActionArea onClick={props.onClick} id={props.id} className="chooseCardArea">
          <CardContent className="chooseCardContentArea">
            <Typography className="chooseCardContent" align="center" variant="body1" color="text.secondary" component="div">
              {props.content}
            </Typography>
            <Typography mt="1" align="center" gutterBottom variant="h5" component="div">
              {props.title}
            </Typography>
          </CardContent>
        </CardActionArea>
      </Tooltip>
    </Card>
  );
}

class CarouselBlock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {current: 0};
  }
  render() {
    let content;

    if(this.props.ideas!=null) {
      let prevSlide;
      let centerSlide;
      let nextSlide;

      console.log(this.props.ideas)

      if(this.state.current === 0) {
        prevSlide = this.props.ideas.length-1;
        centerSlide = this.state.current;
        nextSlide = this.state.current +1;
      } else if(this.state.current === this.props.length-1) {
        prevSlide = this.state.current - 1;
        centerSlide = this.state.current;
        nextSlide = 0;
      } else {
        prevSlide = this.state.current - 1;
        centerSlide = this.state.current;
        nextSlide = this.state.current + 1;
      }

      content = (
        <div>
          <ActionAreaCard title={this.props.ideas[prevSlide].title} content={this.props.ideas[prevSlide].content} />
          <ActionAreaCard title={this.props.ideas[centerSlide].title} content={this.props.ideas[centerSlide].content} />
          <ActionAreaCard title={this.props.ideas[nextSlide].title} content={this.props.ideas[nextSlide].content} />
        </div>
      )
    } else {
      content =  <Box style={{marginTop: "5em"}} sx={{ display: 'flex' }}><CircularProgress /></Box>;
    }

    return (
      <ContentBlock>
        <Title content="Ideas in my list" />
        {content}
      </ContentBlock>
    )
  }
}

export default CarouselBlock;
